let firstTableEls=document.querySelectorAll(".first-table__el");let totalIndustriel=document.querySelector(".first-table__industriel");let secondTableEls=document.querySelectorAll(".second-table__el");let totalRecettes=document.querySelector(".second-table__recettes");let thirdTableEls=document.querySelectorAll(".third-table__el");let totalGain=document.querySelector(".third-table__gain");let adoucissantIndustriel=localStorage.getItem("adoucissant-industriel");let vitresIndustriel=localStorage.getItem("vitres-industriel");let wcIndustriel=localStorage.getItem("wc-industriel");let tablettesIndustriel=localStorage.getItem("tablettes-industriel");let multiIndustriel=localStorage.getItem("multi-industriel");let degraissantIndustriel=localStorage.getItem("degraissant-industriel");let desodorisantIndustriel=localStorage.getItem("desodorisant-industriel");let detartrantIndustriel=localStorage.getItem("detartrant-industriel");let liquideIndustriel=localStorage.getItem("liquide-industriel");let produitsTrue=[{name:"lessive",industriel:localStorage.getItem("lessive-industriel"),recettes:localStorage.getItem("lessive-recettes")},{name:"adoucissant",industriel:localStorage.getItem("adoucissant-industriel"),recettes:localStorage.getItem("adoucissant-recettes")},{name:"vitres",industriel:localStorage.getItem("vitres-industriel"),recettes:localStorage.getItem("vitres-recettes")},{name:"wc",industriel:localStorage.getItem("wc-industriel"),recettes:localStorage.getItem("wc-recettes")},{name:"tablettes",industriel:localStorage.getItem("tablettes-industriel"),recettes:localStorage.getItem("tablettes-recettes")},{name:"multi",industriel:localStorage.getItem("multi-industriel"),recettes:localStorage.getItem("multi-recettes")},{name:"degraissant",industriel:localStorage.getItem("degraissant-industriel"),recettes:localStorage.getItem("degraissant-recettes")},{name:"desodorisant",industriel:localStorage.getItem("desodorisant-industriel"),recettes:localStorage.getItem("desodorisant-recettes")},{name:"detartrant",industriel:localStorage.getItem("detartrant-industriel"),recettes:localStorage.getItem("detartrant-recettes")},{name:"liquide",industriel:localStorage.getItem("liquide-industriel"),recettes:localStorage.getItem("liquide-recettes")}];for(const firstTableEl of firstTableEls){let currentClass=firstTableEl.className.replace("first-table__el","").replace("first-table__","");currentClass=currentClass.trim();let produit=produitsTrue.find(item=>item.name==currentClass);if(produit.industriel==null){firstTableEl.innerHTML="0€"}else{firstTableEl.innerHTML=produit.industriel+"€"}}totalIndustriel.innerHTML=Math.ceil(localStorage.getItem("industriel-number")*100)/100+"€";for(const secondTableEl of secondTableEls){let currentClass=secondTableEl.className.replace("second-table__el","").replace("second-table__","");currentClass=currentClass.trim();let produit=produitsTrue.find(item=>item.name==currentClass);if(produit.recettes==null){secondTableEl.innerHTML="0€"}else{secondTableEl.innerHTML=produit.recettes+"€"}}totalRecettes.innerHTML=Math.ceil(localStorage.getItem("recettes-number")*100)/100+"€";for(const thirdTableEl of thirdTableEls){let currentClass=thirdTableEl.className.replace("third-table__el","").replace("third-table__","");currentClass=currentClass.trim();let produit=produitsTrue.find(item=>item.name==currentClass);if(produit.industriel==null){thirdTableEl.innerHTML="0€"}else{console.log(produit.industriel);console.log(produit.recettes);thirdTableEl.innerHTML=Math.ceil((produit.industriel-produit.recettes)*100)/100+"€"}}let industriel=(Math.ceil(localStorage.getItem("industriel-number")*100)/100).toFixed(2);let recettes=(Math.ceil(localStorage.getItem("recettes-number")*100)/100).toFixed(2);totalGain.innerHTML=(Math.ceil((industriel-recettes)*100)/100).toFixed(2)+"€";