let temoignagesSlider = document.querySelector(".temoignages-list");
let temoignagesEls = document.querySelectorAll(".temoignages-list__el");
let previousButton = document.querySelector(".section-5__button--previous");
let nextButton = document.querySelector(".section-5__button--next");
let compteur = 0;

previousButton.addEventListener("click", previous);
nextButton.addEventListener("click", next);

const hammerSlider = new Hammer(temoignagesSlider);

hammerSlider.on("swipeleft", next);
hammerSlider.on("swiperight", previous);

function next(){
    if (compteur > -2534) {
        compteur = compteur-362;
        temoignagesSlider.style.transform = "translateX("+compteur+"px)";
        console.log(compteur)
    }
}
function previous(){
    if (compteur < 0) {
        compteur = compteur+362;
        temoignagesSlider.style.transform = "translateX("+compteur+"px)";
    }
}